list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX foaf: <http://xmlns.com/foaf/0.1/>
SELECT DISTINCT ?subclass ?maintainership ?software ?softwareName ?dateTime WHERE { ?subject ?property ?maintainership . ?maintainership a core:Maintainership . ?maintainership core:relates ?software . ?software a <http://purl.obolibrary.org/obo/ERO_0000071> . ?software rdfs:label ?softwareName .
OPTIONAL { ?subject ?property ?maintainership . ?maintainership a core:Maintainership ; core:relates ?software . ?software a <http://purl.obolibrary.org/obo/ERO_0000071> . ?software core:dateTimeValue ?dateTimeValue . ?dateTimeValue core:dateTime ?dateTime . }
FILTER ( bound(?software) )
} ORDER BY DESC(?dateTime) ?softwareName
